import React, {useState} from 'react';
import { View, TextInput, Button, StyleSheet, Image, Modal, TouchableOpacity, Text, Dimensions } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

const Login = ({ navigation }) => {

    const correctLogin = false;
    //check login in database

    const[email, setEmail] = useState('');
    const[password, setPassword] = useState('');

    const[isModalVisible, setIsModalVisible] = useState(false);

    const handleLogin = () => {
        
        if(!correctLogin) {
            //pop up saying incorrect login
            console.log("invalid login");
        }
        else if(!isValidEmail(email))
        {
            //pop up saying incorrect format
            console.log("invalid email");
        }
    }

    const handleSignUp = () => {
        //post to database
    }


    return (
        <View style={containerStyles.container}>
            <Text style={containerStyles.menu}>Menu</Text>
            <Text style={containerStyles.title}>FinTrack</Text>
            <Text style={containerStyles.login}>Login</Text>
            <TextInput
                style={containerStyles.input}
                placeholder="Email"
                secureTextEntry = {false}
                onChangeText={text => setEmail(text)}
            />
            <TextInput
                style={containerStyles.input}
                placeholder='Password'
                secureTextEntry = {true}
                onChangeText={text => setPassword(text)}
            />
            <TouchableOpacity 
                style={containerStyles.button}
                onPress={handleLogin}
            >
            <Text style={containerStyles.buttonText}>Submit</Text>
            </TouchableOpacity>
            <Text style={containerStyles.italicBoldText}>Create an Account?</Text>
            {/* make touchable opacity for sign up and reset password */}
            <Text style={containerStyles.linkText}>Sign Up</Text>
            <Text style={containerStyles.italicBoldText}>Forgot Password?</Text>
            <Text style={containerStyles.linkText}>Reset Now</Text>
        </View>
    );
};


function isValidEmail(email){
    const pattern = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;

    
    return pattern.test(email); 
}

const containerStyles = StyleSheet.create({
    container:{
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        padding: 16,
        backgroundColor: 'white',
    },
    input: {
        width:200,
        height:40,
        borderColor:'gray',
        borderWidth:1,
        marginBottom:10,
        paddingHorizontal:8,
        borderRadius:20,
        textAlign:'center',
    },
    button: {
        borderRadius:20,
        padding:8,
        alignItems:'center',
        backgroundColor:'purple',
        borderWidth:1,
        width:100,
        marginBottom:20
    },
    buttonText:{
        color:'white',
    },
    italicBoldText: {
        fontStyle: 'italic',
        fontWeight: 'bold',
    },
    linkText: {
        textDecorationLine: 'underline',
        color: 'blue',
        fontStyle: 'italic',
        fontWeight: 'bold',
    },
    title: {
        position: 'absolute',
        top:130,
        left:10,
        fontSize:24,
    },
    login: {
        textAlign:'center',
        marginBottom:30,
        fontSize:30,
    },
    menu: {
        position:'absolute',
        top:70,
        right:Dimensions.get('window').width - 690,
        width:Dimensions.get('window').width,
        borderRadius:5,
        fontSize:20,
    }
});


export default Login;
